import React from 'react';
import classes from './style.module.css'
import {connect} from "react-redux";
import People from "./People";

const WatchFilmsFunction = (props) => {
    const peopleElements = props.people.name.map(item => <People name={item.name}  />)
    return (
        <div>
            {peopleElements}
        </div>
    )
}

let mapStateToProps = (state) => {
    return {
        people: state.filmsPage.filmsData
    }
}

let WatchFilms = connect(mapStateToProps) (WatchFilmsFunction);

export default WatchFilms;
