import React from 'react';
import classes from './style.module.css';
import Post from "./Post";
import {addPostActionCreator, updateNewPostActionCreator} from "../../../Redux/profile-reducer";
import {connect} from "react-redux";


const MyPosts = (props) => {
    const postsElements = props.initMapStateToProps.map(element => <Post name={element.name}
                                                           age={element.age}
                                                           countLike={element.countLike}
                                                           message={element.message} />)

    const onAddPost = () => {
        props.addPost();
    }
    const onPostChange = (e) => {
        props.updateNewPostActionCreator(e.target.value)
    }

    return (
        <div>
            My posts
            <div className="">
                <div><textarea onChange={onPostChange}
                               value={props.newPostText} />
                </div>
                <button onClick={onAddPost} >Add post</button>
                <button>Remove post</button>
            </div>
            {postsElements}
        </div>
    );
}

let mapStateToProps = (state) => {
    return {
        profilePage: state.dialogsPage
    }
}

let mapDispatchToProps = (dispatch) => {
    return {
        addPost: () => {
            dispatch(addPostActionCreator());
        },
        updateNewPostActionCreator: (text) => {
            dispatch(updateNewPostActionCreator(text));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps) (MyPosts(mapStateToProps, mapDispatchToProps));
