import React from 'react';
import classes from './style.module.css'
import People from "./People";
import StoreContext from "../../../StoreContext";

const BlockFriends = (props) => {
    return (
        <StoreContext.Consumer>
            {
                (store) => {
                    let state = store.getState();
                    const people = state.sidebarPage.friendsData.map(element => <People name={element.name}
                                                                                        ava={element.ava}/>)

                    return (
                        <div className={classes.friends}>
                            <h2 className={classes.title_friends}>Friends</h2>
                            <div className={classes.block_people}>{people}</div>
                        </div>
                    )
                }
            }
        </StoreContext.Consumer>
    )
}
export default BlockFriends;
