import React from 'react';
import classes from './style.module.css';
import DialogItem from "./DialogItem";
import Message from "./Message";


const Dialogs = (props) => {

    const dialogsElements = props.dialogs.map(element => <DialogItem name={element.name} id={element.id} avatar={element.avatar} />);
    const messagesElements = props.message.map(element => <Message message={element.message} id={element.id} />);

    const addMessage = () => {
        props.addMessage();
    };
    const changeText = (e) => {
        props.changeText(e.target.value);
    }

    return (
        <div className={classes.dialogs}>
            <div className={classes.dialogs_items}>
                {dialogsElements}
            </div>
            <div className={classes.messages}>
                {messagesElements}
            </div>
            <div className={classes.text}>
                <div className="">
                    <textarea onChange={changeText} value={props.newMessageText} />
                </div>
                <div>
                    <button onClick={addMessage} >add message</button>
                </div>
            </div>
        </div>

    );
}

export default Dialogs;
